// Escribe aqui la implementación de la solución al problema 2
int iUnos = 0;

int binaria(int n)
{
    if(n == 0)
    {
        return 0;
    }
    else if(n%2 == 0)
    {
        //Es par
        iUnos = binaria(n/2);
    }
    else
    {
        //Es impar
        iUnos = binaria(n/2) + 1;
    }
    return iUnos;
}



