// Algoritmo de Floyd
// Escribe aqui la implementación de la solución al problema 3
int iCount = 0;

int min(int a,int b)
{
    if(a<b)
        return(a);
    return(b);
}

void cuentaTrayectorias(int camino[TAM][TAM], int i, int j, int city)
{
    if(camino[i][j] == i)
    {
        return;
    }
    //Si se pasa por la ciudad dada como entrada, sumar uno y sale.
    if(camino[i][j] == city)
    {
        iCount++;
        return;
    }
    cuentaTrayectorias(camino, i, camino[i][j], city);
}

void floyd(int D[TAM][TAM],int n)
{
    //Inicializar matriz de caminos
    for(int i=0; i<TAM; i++)
    {
        for(int j=0; j<TAM; j++)
        {
            if(j==i)
            {
                camino[i][j] = 0;
            }
            else if(D[i][j] != 99999)
            {
                camino[i][j] = i;
            }
            else
            {
                camino[i][j] = -1;
            }
        }
        
    }
    
    for(int k=0; k<n; k++)
    {
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<n; j++)
            {
                if(D[i][k]+D[k][j]<D[i][j])
                {
                    D[i][j] = D[i][k]+D[k][j];
                    //Asignar nodo por el que pasa con menor costo
                    camino[i][j] = camino[k][j];
                }
            }
        }
    }
}


